import ServicesList from './ServicesList'
import {useState} from "react";
import './services.css';
import services_data from "./Data";


const Services = (props) => {
    const [searchQuery, setSearchQuery] = useState('');

    if (!props.services || !Array.isArray(props.services)) {
        // Handle the case where props.services is not defined or not an array
        return <div>No services data available.</div>;
    }

    const handleSearch = (e) => {
        setSearchQuery(e.target.value);
    };

    const filteredServices = props.services.filter((service) => {
        return service.title.toLowerCase().includes(searchQuery.toLowerCase());
    });

    return (
        <section>
            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Search by title..."
                    value={searchQuery}
                    onChange={handleSearch}
                />
            </div>
            <ServicesList services={filteredServices} />
        </section>
    );
};

export default Services;
