import React, { useState } from 'react';
import Card from "../../../../../UI/Card/Card";
import classes from 'Consultatii.module.css'
const Consultation = ({ consultationData }) => {
    const [showAttachments, setShowAttachments] = useState(false);

    const toggleAttachments = () => {
        setShowAttachments(!showAttachments);
    };

    return (
        <div>
            <div>
                <h3>{consultationData.doctor}</h3>
                <p>Consultation Date: {consultationData.date}</p>
                <button onClick={toggleAttachments}>
                    {showAttachments ? 'Hide Attachments' : 'Show Attachments'}
                </button>
            </div>

            {showAttachments && (
                <div>
                    <h4>Attachments:</h4>
                    <ul>
                        <li>
                            <a href={consultationData.diagnosticPDF} target="_blank" rel="noopener noreferrer">
                                Diagnostic PDF
                            </a>
                        </li>
                        <li>
                            <a href={consultationData.prescriptionPDF} target="_blank" rel="noopener noreferrer">
                                Prescription PDF
                            </a>
                        </li>
                    </ul>
                </div>
            )}
        </div>
    );
};

const Consultatii = () => {
    // Sample consultation data
    const consultations = [
        {
            doctor: 'Dr. Smith',
            date: '2022-01-10',
           /* diagnosticPDF: 'path/to/diagnostic.pdf',
            prescriptionPDF: 'path/to/prescription.pdf',*/
        },
        // Add more consultation data as needed
    ];

    return (
        <Card>
        <div>
            <h2>Consultatii</h2>
            {consultations.map((consultation, index) => (
                <Consultation key={index} consultationData={consultation} />
            ))}
        </div>
        </Card>
    );
};

export default Consultatii;
