import React, { useState } from 'react';
import classes from './Consultatii.module.css';

const Appointment = ({ appointmentData }) => {
    const [showDetails, setShowDetails] = useState(false);

    const toggleDetails = () => {
        setShowDetails(!showDetails);
    };

    return (
        <div className={classes.consultationItem}>
            <div>
                <h3 className={classes.consultationDoctor}>{appointmentData.patientName}</h3>
                <p className={classes.consultationField}>Reason for Checkup: {appointmentData.reason}</p>
                <p className={classes.consultationDate}>Date and Time: {appointmentData.dateTime}</p>
                <p className={classes.consultationField}>Services: {appointmentData.services}</p>
                <button className={classes.closeButton} onClick={toggleDetails}>
                    {showDetails ? 'Hide Details' : 'Show Details'}
                </button>
            </div>

            {showDetails && (
                <div className={classes.attachmentsSection}>
                    <h4>Attachments:</h4>
                    <ul className={classes.attachmentsList}>
                        <li className={classes.attachmentItem}>
                            <a
                                href={appointmentData.reportPDF}
                                target="_blank"
                                rel="noopener noreferrer"
                                className={classes.attachmentLink}
                            >
                                Diagnostic PDF
                            </a>
                        </li>
                        <li className={classes.attachmentItem}>
                            <a
                                href={appointmentData.prescriptionPDF}
                                target="_blank"
                                rel="noopener noreferrer"
                                className={classes.attachmentLink}
                            >
                                Prescription PDF
                            </a>
                        </li>
                    </ul>
                </div>
            )}
        </div>
    );
};

const ProgramariMedic = () => {
    const appointments = [
        {
            patientName: 'John Doe',
            reason: 'General Checkup',
            dateTime: '2024-03-01 09:00 AM',
            services: 'General examination, blood test',
            reportPDF: 'path/to/report.pdf',
            prescriptionPDF: 'path/to/prescription.pdf',
        },
        {
            patientName: 'Jane Smith',
            reason: 'Dental Checkup',
            dateTime: '2024-03-02 10:30 AM',
            services: 'Dental examination, cleaning',
            reportPDF: 'path/to/report.pdf',
            prescriptionPDF: 'path/to/prescription.pdf',
        },
        // Add more appointments as needed
    ];

    const [searchTerm, setSearchTerm] = useState('');

    const filteredAppointments = appointments.filter((appointment) =>
        appointment.patientName.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className={classes.consultatiiContainer}>
            <h2 className={`${classes.titleBackground} ${classes.consultatiiTitle}`}>Programari Medic</h2>

            {/* Search Box */}
            <div className={classes.searchContainer}>
                <input
                    className={classes.search}
                    type="text"
                    placeholder="Search appointments"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>

            {filteredAppointments.map((appointment, index) => (
                <Appointment key={index} appointmentData={appointment} />
            ))}

            <button className={classes.closeButton} onClick={() => window.history.back()}>
                Close
            </button>
        </div>
    );
};

export default ProgramariMedic;
