// ProfileEditPage.js

import React, { useState } from 'react';
import './Profil.css'; // Import CSS file for styling

const ProfileEditPage = () => {
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        photo: null,
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handlePhotoChange = (e) => {
        const file = e.target.files[0];
        setFormData(prevState => ({
            ...prevState,
            photo: file
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Logic to submit form data, e.g., send to backend
        console.log(formData);
        // Reset form after submission
        setFormData({
            name: '',
            description: '',
            photo: null,
        });
    };

    return (
        <div className="profile-edit-container">
            <h2>Edit Profile</h2>
            <form onSubmit={handleSubmit}>
                <div className="profile-picture-container">
                    <label htmlFor="photo" className="profile-picture-label">
                        <img src={formData.photo ? URL.createObjectURL(formData.photo) : 'placeholder.jpg'} alt="Profile" className="profile-picture" />
                        <input type="file" id="photo" accept="image/*" onChange={handlePhotoChange} />
                    </label>
                </div>
                <div className="profile-details">
                    <div>
                        <label>Name:</label>
                        <input type="text" name="name" value={formData.name} onChange={handleInputChange} />
                    </div>
                    <div>
                        <label>Description:</label>
                        <textarea name="description" value={formData.description} onChange={handleInputChange} />
                    </div>
                </div>
                <button type="submit" className="save-button">Save</button>
            </form>
        </div>
    );
};

export default ProfileEditPage;
