// Programeaza.js
import React, { useState } from 'react';
import SelectMedic from './SelectMedic';
import SelectService from './SelectService';
import RequestMeeting from './RequestMeeting';

function Programeaza() {
    const [selectedMedic, setSelectedMedic] = useState(null);
    const [selectedService, setSelectedService] = useState(null);
    const [selectedDateTime, setSelectedDateTime] = useState(null);

    const handleMedicSelection = (medic) => {
        setSelectedMedic(medic);
    };

    const handleServiceSelection = (service) => {
        setSelectedService(service);
    };

    const handleDateTimeSelection = (dateTime) => {
        setSelectedDateTime(dateTime);
    };

    const handleMeetingRequest = () => {
        // Handle the logic for meeting request with selectedMedic, selectedService, and selectedDateTime
        console.log('Meeting requested with:');
        console.log('Medic:', selectedMedic);
        console.log('Service:', selectedService);
        console.log('Date and Time:', selectedDateTime);
    };

    return (
        <div>
            <SelectMedic onSelectMedic={handleMedicSelection} />
            {selectedMedic && (
                <>
                    <DateTimePicker onSelectDate={handleDateTimeSelection} onSelectTime={handleDateTimeSelection} />
                    <SelectService onSelectService={handleServiceSelection} />
                    {selectedService && selectedDateTime && (
                        <RequestMeeting
                            medic={selectedMedic}
                            service={selectedService}
                            dateTime={selectedDateTime}
                            onRequestMeeting={handleMeetingRequest}
                        />
                    )}
                </>
            )}
        </div>
    );
}

export default Programeaza;
