// AllPatients.js

import React, { useState } from 'react';
import PatientsList from './PatientsList';
import classes from './AllPatients.module.css'; // Import the CSS module

const patientsData = [
    {
        name: 'John Doe',
        age: 30,
        lastVisitReason: 'Check-up',
    },
    // Add more patient data as needed
];

function AllPatients() {
    const [searchTerm, setSearchTerm] = useState('');

    const filteredPatients = patientsData.filter((patient) =>
        patient.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <section>
            <div className={classes.titleContainer}>
                <h1 className={classes.headerPat}>All Patients</h1>
            </div>

            <div className={classes.searchContainer}>
                <input
                    className={classes.search}
                    type="text"
                    placeholder="Search patients"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>

            <div className={classes.listContainer}>
                <PatientsList patients={filteredPatients} />

                <button className={classes.closeButtonLeft} onClick={() => window.history.back()}>
                    Close
                </button>
            </div>
        </section>
    );
}

export default AllPatients;
