// SelectMedic.js
import React, { useState } from 'react';
import MedicsList from '../../../../beforeLogIn/Medics/MedicsList';
import classes from './SelectMedic.module.css'


const medicsData = [
    {
        name: 'Andrei Lovin',
        description: 'Urologist',
        image: 'https://example.com/andrei.jpg',
        location: 'Bucuresti',
        rating: '5'
    },
    {
        name: 'Maria Rodriguez',
        description: 'Pediatrician',
        image: 'https://example.com/maria.jpg',
        location: 'Bucuresti',
        rating: '5'
    },
    {
        name: 'John Smith',
        description: 'Cardiologist',
        image: 'https://example.com/john.jpg',
        location: 'Bucuresti',
        rating: '5'
    },
    {
        name: 'Sarah Lee',
        description: 'Dermatologist',
        image: 'https://example.com/sarah.jpg',
        location: 'Iasi',
        rating: '5'
    },
    {
        name: 'Michael Chang',
        description: 'Orthopedic Surgeon',
        image: 'https://example.com/michael.jpg',
        location: 'Iasi',
        rating: '5'
    },
    {
        name: 'Emily Davis',
        description: 'Neurologist',
        image: 'https://example.com/emily.jpg',
        location: 'Iasi',
        rating: '5'
    },

];

function SelectMedic({ onSelectMedic }) {
    const [searchTerm, setSearchTerm] = useState('');

    const handleMedicSelection = (medic) => {
        onSelectMedic(medic);
    };

    return (
        <section>
            <h1 className={classes.header}>Select Medic</h1>
            <input
                className={classes.search}
                type="text"
                placeholder="Search medics"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />
            <MedicsList medics={medicsData} searchTerm={searchTerm} onSelectMedic={handleMedicSelection} />

            {/* Add a button to select the medic */}
            <button
                className={classes.selectButton}
                onClick={() => {
                    // You can handle the selection logic here or use the onSelectMedic callback
                    console.log('Medic selected');
                }}
            >
                Select Medic
            </button>
        </section>
    );
}

export default SelectMedic;
