// Appointment.js

import React, { useState } from 'react';
import classes from './ProgramariMedic.module.css';

const Appointment = ({ appointmentData, onConsultationDone, onDiagnosticPDFUpload, onPrescriptionPDFUpload }) => {
    const [showDetails, setShowDetails] = useState(false);

    const toggleDetails = () => {
        setShowDetails(!showDetails);
    };

    const handleDiagnosticPDF = () => {
        onDiagnosticPDFUpload(appointmentData.id);
    };

    const handlePrescriptionPDF = () => {
        onPrescriptionPDFUpload(appointmentData.id);
    };

    return (
        <div className={classes.appointmentItem}>
            <div>
                <h3 className={classes.appointmentDoctor}>{appointmentData.doctor}</h3>
                <p className={classes.appointmentDate}>Date: {appointmentData.date}</p>
                <p className={classes.appointmentField}>Field: {appointmentData.field}</p>
                <p>Reason for Checkup: {appointmentData.reasonForCheckup}</p>
                <p>Services: {appointmentData.services.join(', ')}</p>
                <button onClick={toggleDetails} className={classes.showHideButton}>
                    {showDetails ? 'Hide Details' : 'Show Details'}
                </button>
            </div>

            {showDetails && (
                <div className={classes.detailsSection}>
                    <button onClick={handleDiagnosticPDF} className={classes.uploadButton}>
                        Upload Diagnostic PDF
                    </button>
                    <button onClick={handlePrescriptionPDF} className={classes.uploadButton}>
                        Upload Prescription PDF
                    </button>
                </div>
            )}

            <button onClick={() => onConsultationDone(appointmentData.id)} className={classes.markAsDoneButton}>
                Mark as Done
            </button>
        </div>
    );
};

export default Appointment;
