// Programeaza.js
import React, { useState } from 'react';
import MedicsList from '../../../../beforeLogIn/Medics/MedicsList';
import ServicesList from '../../../../beforeLogIn/Medics/MedicsList';
import DateTimePicker from './DateTimePicker';
import classes from './Programeaza.module.css';
import './ProgrameazaStyles.css'; // Import the combined styles

const medicsData = [
    // ... (your medic data)
];

const servicesData = [
    // ... (your service data)
];

function Programeaza() {
    const [selectedMedic, setSelectedMedic] = useState(null);
    const [selectedService, setSelectedService] = useState(null);
    const [meetingType, setMeetingType] = useState('online');
    const [selectedDate, setSelectedDate] = useState(null);
    const [selectedTime, setSelectedTime] = useState(null);

    const handleMedicSelection = (medic) => {
        setSelectedMedic(medic);
    };

    const handleServiceSelection = (service) => {
        setSelectedService(service);
    };

    const handleMeetingTypeChange = (event) => {
        setMeetingType(event.target.value);
    };

    const handleDateChange = (date) => {
        setSelectedDate(date);
    };

    const handleTimeChange = (time) => {
        setSelectedTime(time);
    };

    const handleRequestMeeting = () => {
        // Add logic to handle meeting request
        console.log('Meeting requested:', {
            medic: selectedMedic,
            service: selectedService,
            meetingType: meetingType,
            date: selectedDate,
            time: selectedTime
        });
    };

    return (
        <div className={classes.container}>
            <section className={classes.selectMedicSection}>
                <h1 className={classes.header}>Select Medic</h1>
                <MedicsList medics={medicsData} onSelectMedic={handleMedicSelection} />
            </section>

            <section className={classes.selectServiceSection}>
                <h1 className={classes.header}>Select Service</h1>
                <ServicesList services={servicesData} onSelectService={handleServiceSelection} />
            </section>

            <section className={classes.selectMeetingTypeSection}>
                <h1 className={classes.header}>Select Meeting Type</h1>
                <div className={classes.meetingTypeOptions}>
                    <label>
                        <input
                            type="radio"
                            value="online"
                            checked={meetingType === 'online'}
                            onChange={handleMeetingTypeChange}
                        />
                        Online
                    </label>
                    <label>
                        <input
                            type="radio"
                            value="inPerson"
                            checked={meetingType === 'inPerson'}
                            onChange={handleMeetingTypeChange}
                        />
                        In Person
                    </label>
                </div>
            </section>

            <section className={classes.selectDateTimeSection}>
                <h1 className={classes.header}>Select Date and Time</h1>
                <DateTimePicker onSelectDate={handleDateChange} onSelectTime={handleTimeChange} />
            </section>

            <section className={classes.requestMeetingSection}>
                <button className={classes.requestMeetingButton} onClick={handleRequestMeeting}>
                    Request Meeting
                </button>
            </section>
        </div>
    );
}

export default Programeaza;
