import React, { useState } from 'react';
import Card from "../../../../../UI/Card/Card";
import classes from './Rezultate.module.css';

const LabResult = ({ labResultData }) => {
    const [showDetails, setShowDetails] = useState(false);

    const toggleDetails = () => {
        setShowDetails(!showDetails);
    };

    const LabResultDetails = () => (
        <div className={classes.detailsSection}>
            <h4>Details:</h4>
            <p className={classes.detailsText}>{labResultData.details}</p>
        </div>
    );

    return (
        <div className={classes.labResultItem}>
            <div>
                <h3 className={classes.labResultTitle}>{labResultData.title}</h3>
                <p className={classes.labResultDate}>Result Date: {labResultData.date}</p>
                <button className={classes.showHideButton} onClick={toggleDetails}>
                    {showDetails ? 'Hide Details' : 'Show Details'}
                </button>
            </div>

            {showDetails && <LabResultDetails />}

            {labResultData.attachments && (
                <div className={classes.attachmentsSection}>
                    <h4>Attachments:</h4>
                    <ul className={classes.attachmentsList}>
                        {labResultData.attachments.map((attachment, attachmentIndex) => (
                            <li key={attachmentIndex} className={classes.attachmentItem}>
                                <a
                                    href={attachment.path}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className={classes.attachmentLink}
                                >
                                    {attachment.name}
                                </a>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

const LabResults = () => {
    const labResults = [
        {
            title: 'Blood Test',
            date: '2022-01-15',
            details: 'Hemoglobin: 14.2 g/dL, White Blood Cells: 7,000/μL, Platelets: 250,000/μL',
            attachments: [
                { name: 'Lab Result 1', path: 'path/to/lab_result_1.pdf' },
                { name: 'Additional Document', path: 'path/to/lab_result_1_additional.pdf' },
            ],
        },
        {
            title: 'Urinalysis',
            date: '2022-01-16',
            details: 'pH: 6.0, Protein: Negative, Glucose: Negative',
            attachments: [
                { name: 'Lab Result 2', path: 'path/to/lab_result_2.pdf' },
            ],
        },
        // Add more lab result data as needed
    ];

    return (
        <Card>
            <div className={classes.labResultsContainer}>
                <h2 className={classes.labResultsTitle}>Lab Results</h2>
                {labResults.map((labResult, index) => (
                    <LabResult key={index} labResultData={labResult} />
                ))}
            </div>
        </Card>
    );
};

export default LabResults;
