// Programeaza.js

import React, { useState } from 'react';
import AllMedics from '../../../../beforeLogIn/Medics/AllMedics';
import SelectMedic from './SelectMedic';
import SelectMeetingType from './SelectMeetingType';
import SelectService from './SelectService';
import SelectDateTime from './SelectDateTime';

const Programeaza = () => {
    const [step, setStep] = useState(1); // Initialize with the first step
    const [selectedMedic, setSelectedMedic] = useState(null);
    const [meetingType, setMeetingType] = useState('');
    const [selectedService, setSelectedService] = useState('');
    const [selectedDateTime, setSelectedDateTime] = useState('');

    const handleNextStep = (data) => {
        switch (step) {
            case 1:
                setSelectedMedic(data.selectedMedic);
                break;
            case 2:
                setMeetingType(data.meetingType);
                break;
            case 3:
                setSelectedService(data.selectedService);
                break;
            case 4:
                setSelectedDateTime(data.selectedDateTime);
                break;
            default:
                break;
        }

        setStep((prevStep) => prevStep + 1);
    };

    const renderStep = () => {
        switch (step) {
            case 1:
                return <SelectMedic onNext={handleNextStep} />;
            case 2:
                return <SelectMeetingType onNext={handleNextStep} />;
            case 3:
                return <SelectService onNext={handleNextStep} />;
            case 4:
                return <SelectDateTime onFinish={handleNextStep} />;
            default:
                return <AllMedics />;
        }
    };

    return (
        <div>
            <h1>Your Appointment Scheduler</h1>
            {renderStep()}
        </div>
    );
};

export default Programeaza;
