import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import classes from './SignUp.module.css';
import logo from '../LogIn/logo.png'

function SignUp() {
    const [formData, setFormData] = useState({
        firstName: '',
        familyName: '',
        cnp: '',
        dateOfBirth: '',
        password: '',
        repeatPassword: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;

        // Format date of birth as dd/mm/yyyy
        if (name === 'dateOfBirth' && value.length === 8) {
            const formattedDate = value.replace(/(\d{2})(\d{2})(\d{4})/, '$1/$2/$3');
            setFormData({
                ...formData,
                [name]: formattedDate,
            });
        } else {
            setFormData({
                ...formData,
                [name]: value,
            });
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Add validation logic here
        if (formData.password !== formData.repeatPassword) {
            alert('Passwords do not match!');
            return;
        }

        // Add logic for submitting the form
        console.log('Form submitted:', formData);
    };

    return (
        <div className={classes.signUpContainer}>
            <div className={classes.logoContainer}>
                <Link to="/">
                    <img src={logo} alt="Logo" className={classes.logo} />
                </Link>
            </div>
            <div className={classes.formContainer}>
                <form className={classes.signUpForm} onSubmit={handleSubmit}>
                    {/* ... other input fields ... */}

                    <label htmlFor="dateOfBirth" className={classes.label}>
                        Date of Birth (dd/mm/yyyy):
                    </label>
                    <input
                        type="text" // Change type to text
                        id="dateOfBirth"
                        name="dateOfBirth"
                        value={formData.dateOfBirth}
                        onChange={handleChange}
                        className={classes.input}
                        placeholder="dd/mm/yyyy"
                        maxLength="10"
                        required
                    />

                    <button type="submit" className={classes.signupButton}>
                        Sign Up
                    </button>
                </form>
            </div>
            <div className={classes.loginLink}>
                Already have an account? <Link to="/login">Log In</Link>
            </div>
        </div>
    );
}

export default SignUp;
