// AllPatientsList.js

import React, { useState } from 'react';
import PatientItem from './PatientItem';
import classes from './AllPatientsList.module.css';

const patients_data = [
    {
        name: 'John Doe',
        age: 35,
        reasonForVisit: 'Routine Checkup',
    },
    // ... (other patients with different details)
];

function AllPatientsList() {
    const [searchTerm, setSearchTerm] = useState('');

    const filteredPatients = patients_data.filter((patient) =>
        patient.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <section className={classes.listContainer}>
            <h1 className={classes.title}>All Patients</h1>
            <div className={classes.searchContainer}>
                <input
                    className={classes.search}
                    type="text"
                    placeholder="Search patients"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>

            {filteredPatients.length > 0 ? (
                <ul>
                    {filteredPatients.map((patient, index) => (
                        <PatientItem
                            key={index}
                            name={patient.name}
                            age={patient.age}
                            reasonForVisit={patient.reasonForVisit}
                        />
                    ))}
                </ul>
            ) : (
                <p>No patients found.</p>
            )}
        </section>
    );
}

export default AllPatientsList;
