// AllPatients.js

import React, { useState } from 'react';
import PatientsList from './PatientsList';
import classes from './AllPatients.module.css';

const patientsData = [
    {
        name: 'John Doe',
        age: 30,
        lastVisitReason: 'Check-up',
    },
    // Add more patient data as needed
];

function AllPatients() {
    const [searchTerm, setSearchTerm] = useState('');

    const filteredPatients = patientsData.filter((patient) =>
        patient.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <section>
            <h1 className={classes.headerPat}>All Patients</h1>
            <input
                className={classes.search}
                type="text"
                placeholder="Search patients"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />

            <div className={classes.listContainer}>
                <PatientsList patients={filteredPatients} />
            </div>

            <button className={classes.closeButton} onClick={() => window.history.back()}>
                Close
            </button>
        </section>
    );
}

export default AllPatients;
