// AllPatients.js

import React, { useState } from 'react';
import classes from './AllPatients.module.css'; // Import the CSS module

const PatientItem = ({ patientData }) => {
    return (
        <div className={`${classes.patientItem} ${classes.whiteBackground}`}>
            <h1 className={classes.name}>{patientData.name}</h1>
            <p className={classes.age}>Age: {patientData.age}</p>
            <p className={classes.lastVisitReason}>Last Visit Reason: {patientData.lastVisitReason}</p>
        </div>
    );
};

const PatientsList = ({ patients }) => {
    return (
        <div className={classes.listContainer}>
            {patients.map((patient, index) => (
                <PatientItem
                    key={index}
                    patientData={patient}
                />
            ))}

            <button className={classes.closeButton} onClick={() => window.history.back()}>
                Close
            </button>
        </div>
    );
};

const AllPatients = () => {
    const patientsData = [
        {
            name: 'John Doe',
            age: 30,
            lastVisitReason: 'Check-up',
        },
        // Add more patient data as needed
    ];

    const [searchTerm, setSearchTerm] = useState('');

    const filteredPatients = patientsData.filter((patient) =>
        patient.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div>
            <h2 className={classes.allPatientsTitle}>All Patients</h2>

            <div className={classes.searchContainer}>
                <input
                    className={classes.search}
                    type="text"
                    placeholder="Search patients"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>

            <PatientsList patients={filteredPatients}  className={`${classes.allPatientsContainer} ${classes.whiteBackground}`}/>
        </div>
    );
};

export default AllPatients;
